import React from "react";
// Header component
const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  );
};
// Content component
const Content = (props) => {
  return (
    <>
      <Part part={props.part[0]} />
      <Part part={props.part[1]} />
      <Part part={props.part[2]} />
    </>
  );
};

const Part = (props) => {
  return (
    <>
      <p>
        {props.part.name} {props.part.exercisess}
      </p>
    </>
  );
};
// Total component
const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises{" "}
        {props.part[0].exercises +
          props.part[1].exercises +
          props.part[2].exercises}
      </p>
    </>
  );
};
// Main root component
const App = () => {
  const course = {
    header: "Half Stack application development",

    parts: [
      {
        name: "Fundamentals of React",
        exercises: 10,
      },
      {
        name: "Using props to pass data",
        exercises: 10,
      },
      {
        name: "State of a component",
        exercises: 14,
      },
    ],
  };

  return (
    <div>
      <Header course={header} />
      <Content parts={parts} />
      <Total total={parts} />
    </div>
  );
};

export default App;
